var handleBootstrapWizardsValidation = function () {
    "use strict"; $("#wizard").bwizard({
        validating: function (e, t) {

            if (orderId == 0) {
                if (t.index == 0) {
                    if ($('#CustomerId').val() == "" || $("#ContactId").val() == "" || $("#Brand").val() == "" || $("#Description").val() == "") {
                        $("#modal-alert").modal('show');
                        $('#message').text("请完善订单的基本信息");
                        return false;
                    }
                    else {
                        orderId = 21;

                        $.ajax({
                            type: 'POST',
                            url: '/DF_OwnOrder/CreateOrder',
                            async: false,
                            data: {
                                CustomerId: parseInt($('#CustomerId').val()), ContactId: parseInt($('#CustomerId').val()), PlayTime: parseInt($('#PlayTime').val()),
                                PlayCount: parseInt($('#PlayCount').val()), Description: $("#Brand").val() + "-" + $("#Description").val(), startTime: $("#BaseDate").val(),
                                weeks: parseInt($('#PublicWeeks').val())
                            },
                            dataType: 'JSON',
                            success: function (datas) {
                                if (datas.success) {
                                    orderId = datas.orderId;
                                    $("#spanCustomerName").text($('#CustomerId').find("option[value=" + $('#CustomerId').val() + "]").text());
                                    $("#spanContact").text($('#ContactId').find("option[value=" + $('#ContactId').val() + "]").text());
                                    $("#tdDescription").text($('#Brand').find("option[value=" + $('#Brand').val() + "]").text() + "-(" + $('#Description').val() + ")");
                                    $("#spanPlayCount").text($('#PlayTime').val());
                                    $("#tdpublicDate").text($('#publicDate').text());

                                }
                                else {
                                    $("#modal-alert").modal('show');
                                    $('#message').text(datas.message);
                                    return false;
                                }

                            }
                        });
                    }
                }

            }
        },
        activeIndexChanged: function (e, ui) {
            if (ui.index == 1) {

            }
            if (ui.index == 2) {

            }
        }
    })
}; var FormWizardValidationCreate = function () { "use strict"; return { init: function () { handleBootstrapWizardsValidation() } } }()

